export interface Location {
    column: number;
    line: number;
}
export interface Value {
    value: string;
    loc: Location;
}
export interface BaseDeclaration {
    name: string;
    value: string;
    keyLoc: Location;
    valLoc: Location;
}
export interface VariableDeclaration extends BaseDeclaration {
    type: 'css_var';
    defaultValue?: string;
    defaultValueMap?: Record<string, string>;
}
export type Declaration = BaseDeclaration | VariableDeclaration;
export interface StyleRule {
    type: 'StyleRule';
    selectorText: Value;
    variables: Record<string, string>;
    style: Declaration[];
}
export interface FontFaceRule {
    type: 'FontFaceRule';
    style: Declaration[];
}
export interface ImportRule {
    type: 'ImportRule';
    href: string;
    origin: string;
}
export interface KeyframesStyle {
    keyText: Value;
    style: Declaration[];
}
export interface KeyframesRule {
    type: 'KeyframesRule';
    name: Value;
    styles: KeyframesStyle[];
}
export type LynxStyleNode = StyleRule | FontFaceRule | ImportRule | KeyframesRule;
