import { h } from "preact";
import { Icon } from "../icons";
export function Checkbox(props) {
    return (<label class="checkbox-root">
			<span class="checkbox-wrapper">
				<input type="checkbox" checked={props.checked} data-testid={props.testId} onInput={e => props.onChange(e.target.checked)}/>
				<Icon icon={props.checked ? "checkbox-checked" : "checkbox-unchecked"}/>
			</span>
			<span class="checkbox-content">{props.children}</span>
		</label>);
}
