import { h } from "preact";
export function IconBtn(props) {
    return (<button type="button" class="icon-btn" data-kind={props.styling || null} data-active={props.active} title={props.title} disabled={props.disabled} data-testid={props.testId} onClick={e => {
            e.stopPropagation();
            if (props.onClick)
                props.onClick();
        }}>
			<span class="icon-btn-inner" tabIndex={-1} style={props.color ? "color: " + props.color : undefined}>
				{props.children}
				<span class="icon-btn-bg"/>
			</span>
		</button>);
}
