import { h } from "preact";
import { useCallback } from "preact/hooks";
export function SmallTab(props) {
    const onClick = useCallback(() => {
        props.onClick(props.value);
    }, [props]);
    return (<label class="tab">
			<input class="tab-input" type="radio" name={props.name} value={props.value} checked={props.checked} onClick={onClick}/>
			<span class="tab-title">{props.children}</span>
		</label>);
}
export function SmallTabGroup(props) {
    return <div class={`tab-group ${props.class || ""}`}>{props.children}</div>;
}
export function IconTab(props) {
    const onClick = useCallback(() => {
        props.onClick(props.value);
    }, [props]);
    return (<label class="tab-icon-wrapper">
			<input class="tab-input" type="radio" name={props.name} value={props.value} checked={props.checked} onClick={onClick} disabled={props.disabled}/>
			<span class="tab-icon-inner">
				{props.icon}
				<span class="tab-icon-label">{props.children}</span>
			</span>
		</label>);
}
