import { h } from "preact";
export function Hoc(props) {
    return (<span class="hoc hoc-item" data-size={props.small ? "small" : null}>
			{props.children}
		</span>);
}
export function HocPanel(props) {
    return (<div class="hoc-panel" data-testid="hoc-panel">
			{props.hocs.map((hoc, i) => (<Hoc key={i}>{hoc}</Hoc>))}
		</div>);
}
